int
handle_nodeHost(netsnmp_mib_handler *handler,
                          netsnmp_handler_registration *reginfo,
                          netsnmp_agent_request_info   *reqinfo,
                          netsnmp_request_info         *requests)
{
    FILE *fp;
    char node_host[100];
    char temp[100];
    char filename[128];
    char buf[30];
    char *s;

    memset(filename, 0, sizeof(filename));
    strcpy(filename, "/tmp/node.txt");
    memset(buf, 0, sizeof(buf)); 
    strcpy(buf, "hostname");
    memset(node_host, 0, sizeof(node_host));

    switch(reqinfo->mode) {

        case MODE_GET:
            if ((fp = fopen(filename, "r")) != NULL)
            {
                while(fgets(temp, sizeof(temp), fp) != NULL)
                {
                    /*Check if temp has buf*/
                    s = strstr(temp, buf);
                    if ( s != NULL)
                    {
                         break;
                    }
                }
                /*Check if temp has buf*/
                if (s != NULL)
                {
                    /*The fgets return '\n',so just remove it*/
                    if (temp[strlen(temp) - 1] == '\n')
                    {
                        temp[strlen(temp) - 1] = '\0';
                    }  
                    strcpy(node_host, temp);
                    printf("%s\n", node_host);  
                }      
                fclose(fp);                     
            }
            else
            {
                perror("Can't open file");
            }

            snmp_set_var_typed_value(requests->requestvb, ASN_OCTET_STR,
                                     node_host,
                                     strlen(node_host));
            break;


        default:
            /* we should never get here, so this is a really bad error */
            snmp_log(LOG_ERR, "unknown mode (%d) in handle_nodeHost\n", reqinfo->mode );
            return SNMP_ERR_GENERR;
    }

    return SNMP_ERR_NOERROR;
}
